import React from "react";
import "./Video.css";
import { AiFillHeart } from "react-icons/ai";
import { FaCommentDots, FaShare, FaMusic } from "react-icons/fa";

const VideoInfo = () => {
  return (
    <>
      <div className="video_info_wrapper">
        <img
          src="https://topchiase24h.com/wp-content/uploads/2021/04/hinh-nen-anime-girl-cho-iphone-1.jpg"
          className="avatar-info"
          alt="Avatar"
        />
        <div className="video_info_user">
          <div className="user_info_wrapper">
            <span className="user_id">Phuongcf1998</span>
            <span className="user_name">Phương Goodboy</span>
          </div>
          <div className="video_info_tag">
            <span className="tag_id">#fyp #xuhuong #meme </span>
          </div>
          <div className="tag_music">
            {" "}
            <span>
              <FaMusic />
            </span>{" "}
            Originalton - 🔥Yujiro🔥{" "}
          </div>
        </div>
        <button className="btn btn-follow">Follow</button>
      </div>
    </>
  );
};

const VideoContent = () => {
  return (
    <>
      <div className="video_content-wrapper">
        <video
          alt="Video"
          className="video_content-clip"
          src="https://v16-webapp.tiktok.com/9561b974ebb42d9a17db6ed7c5e6acb1/6255dc01/video/tos/useast2a/tos-useast2a-pve-0037-aiso/962ed22e02b04fafb9b2fdd7049f8d9b/?a=1988&br=2090&bt=1045&cd=0%7C0%7C1%7C0&ch=0&cr=0&cs=0&cv=1&dr=0&ds=3&er=&ft=eXd.6Hk_Myq8Zv82hwe2NlAnml7Gb&l=202204121406320102450101811E191823&lr=tiktok_m&mime_type=video_mp4&net=0&pl=0&qs=0&rc=and2dzc6Zmh0OzMzZjgzM0ApZ2Y8OzY5NTxlN2c4NjU0N2c0cGpvcjQwLjRgLS1kL2Nzc2AtYF80MWNjYDViLmIxNDY6Yw%3D%3D&vl=&vr="
        />
        <div className="video_content-emoji">
          <span className="video_content-icon">
            <AiFillHeart />
          </span>
          <span>13.9k</span>
          <span className="video_content-icon">
            <FaCommentDots />
          </span>
          <span>512</span>
          <span className="video_content-icon">
            <FaShare />
          </span>
          <span>103</span>
        </div>
      </div>
    </>
  );
};

export default function Video({ url, options }) {
  return (
    <div className="video-wrapper">
      <VideoInfo />
      <VideoContent url={url} />
    </div>
  );
}
