import React, { useRef, useState } from "react";
import "./Video.css";
import { AiFillHeart } from "react-icons/ai";
import { FaCommentDots, FaShare, FaMusic, FaWhatsapp } from "react-icons/fa";
import { ImEmbed2 } from "react-icons/im";
import { FiSend } from "react-icons/fi";
import { BsFacebook } from "react-icons/bs";

const VideoInfo = ({ urlAvatar, userName, userId, musicName, tagId }) => {
  return (
    <>
      <div className="video_info_wrapper">
        <img src={urlAvatar} className="avatar-info" alt="Avatar" />
        <div className="video_info_user">
          <div className="user_info_wrapper">
            <span className="user_id">{userId}</span>
            <span className="user_name">{userName}</span>
          </div>
          <div className="video_info_tag">
            <span className="tag_id">{tagId} </span>
          </div>
          <div className="tag_music">
            {" "}
            <span>
              <FaMusic />
            </span>{" "}
            {musicName}{" "}
          </div>
        </div>
        <button className="btn btn-follow">Follow</button>
      </div>
    </>
  );
};

const VideoSharePopUp = () => {
  return <></>;
};

const VideoContent = ({ url }) => {
  const [isPlaying, setPlaying] = useState(false);
  const [isSubscribe, setSubscribed] = useState(false);
  const videoRef = useRef();
  const iconHeart = useRef();

  const handleShareIcon = () => {
    // alert("Subscribed")
    if (isSubscribe === false) {
      setSubscribed(true);
      setTimeout(() => {
        setSubscribed(false);
      }, 3000);
    } else {
      setSubscribed(false);
    }
  };

  const playVideo = () => {
    if (isPlaying == false) {
      videoRef.current.play();
      setPlaying(true);
    } else {
      videoRef.current.pause();
      setPlaying(false);
    }
  };

  return (
    <>
      <div className="video_content-wrapper">
        <video
          controls
          ref={videoRef}
          onClick={playVideo}
          loop
          alt="Video"
          className="video_content-clip"
          src={url}
        />

        <div className="video_content-emoji">
          <span className="video_content-icon">
            <AiFillHeart
              onClick={handleShareIcon}
              style={isSubscribe ? { color: "red" } : {}}
            />
          </span>
          <span>13.9k</span>
          <span className="video_content-icon">
            <FaCommentDots />
          </span>
          <span>512</span>

          <span className="video_content-icon">
            <FaShare className="share-icon" />
            <div className="video_share-wrapper">
              <ul className="video_share-list">
                <li className="video_share-item">
                  <span className="video_share-item-icon">
                    <ImEmbed2 />
                  </span>
                  <a href="#" className="video_share-item-link">
                    Nhúng
                  </a>
                </li>
                <li className="video_share-item">
                  <span className="video_share-item-icon">
                    <BsFacebook />
                  </span>
                  <a href="#" className="video_share-item-link">
                    Chia sẻ facebook
                  </a>
                </li>
                <li className="video_share-item">
                  <span className="video_share-item-icon">
                    <FaWhatsapp />
                  </span>
                  <a href="#" className="video_share-item-link">
                    Chia sẻ Whatsapp
                  </a>
                </li>
                <li className="video_share-item">
                  <span className="video_share-item-icon">
                    <FiSend />
                  </span>
                  <a href="#" className="video_share-item-link">
                    Gửi cho bạn bè
                  </a>
                </li>
              </ul>
            </div>
          </span>

          <span>103</span>
        </div>
      </div>
    </>
  );
};

export default function Video({
  urlVideo,
  urlAvatar,
  userName,
  userId,
  musicName,
  tagId,
}) {
  return (
    <div className="video-wrapper">
      <VideoInfo
        urlAvatar={urlAvatar}
        userName={userName}
        userId={userId}
        musicName={musicName}
        tagId={tagId}
      />
      <VideoContent url={urlVideo} />
    </div>
  );
}
